selector_to_html = {"a[href=\"#v1-1-2\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\"><a class=\"reference external\" href=\"https://github.com/molmod/stacie/releases/tag/v1.1.2\">1.1.2</a> - 2025-11-19<a class=\"headerlink\" href=\"#v1-1-2\" title=\"Link to this heading\">\u00b6</a></h2><p>Additional packaging cleanups.</p>", "a[href=\"#id3\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\">Changed<a class=\"headerlink\" href=\"#id3\" title=\"Link to this heading\">\u00b6</a></h3>", "a[href=\"#rc1-2025-06-25\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\"><a class=\"reference external\" href=\"https://github.com/molmod/stacie/releases/tag/v1.0.0rc1\">1.0.0rc1</a> - 2025-06-25<a class=\"headerlink\" href=\"#rc1-2025-06-25\" title=\"Link to this heading\">\u00b6</a></h2><p>This is the first release candidate of STACIE, with a final release expected very soon.\nThe main remaining issues are related to (back)linking of external resources\nin the documentation and README files.</p>", "a[href=\"#v1-2-0\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\"><a class=\"reference external\" href=\"https://github.com/molmod/stacie/releases/tag/v1.2.0\">1.2.0</a> - 2025-12-23<a class=\"headerlink\" href=\"#v1-2-0\" title=\"Link to this heading\">\u00b6</a></h2><p>Reformulation of the Lorentz model parameters.</p>", "a[href=\"#changed\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\">Changed<a class=\"headerlink\" href=\"#changed\" title=\"Link to this heading\">\u00b6</a></h3>", "a[href=\"#v1-1-3\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\"><a class=\"reference external\" href=\"https://github.com/molmod/stacie/releases/tag/v1.1.3\">1.1.3</a> - 2025-11-19<a class=\"headerlink\" href=\"#v1-1-3\" title=\"Link to this heading\">\u00b6</a></h2><p>Support for Python 3.10</p>", "a[href=\"#v1-0-0\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\"><a class=\"reference external\" href=\"https://github.com/molmod/stacie/releases/tag/v1.0.0\">1.0.0</a> - 2025-06-26<a class=\"headerlink\" href=\"#v1-0-0\" title=\"Link to this heading\">\u00b6</a></h2><p>This is the first stable release of STACIE!</p>", "a[href=\"#id7\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\">Changed<a class=\"headerlink\" href=\"#id7\" title=\"Link to this heading\">\u00b6</a></h3>", "a[href=\"#v1-1-0\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\"><a class=\"reference external\" href=\"https://github.com/molmod/stacie/releases/tag/v1.1.0\">1.1.0</a> - 2025-11-10<a class=\"headerlink\" href=\"#v1-1-0\" title=\"Link to this heading\">\u00b6</a></h2><p>Improved Lorentz model, with a more robust estimate of the exponential correlation time and its uncertainty.\nImproved examples.</p>", "a[href=\"#id11\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\">Changed<a class=\"headerlink\" href=\"#id11\" title=\"Link to this heading\">\u00b6</a></h3>", "a[href=\"#v1-2-1\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\"><a class=\"reference external\" href=\"https://github.com/molmod/stacie/releases/tag/v1.2.1\">1.2.1</a> - 2025-12-28<a class=\"headerlink\" href=\"#v1-2-1\" title=\"Link to this heading\">\u00b6</a></h2><p>Minor documentation and dependency improvements.</p>", "a[href=\"#unreleased\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\"><a class=\"reference external\" href=\"https://github.com/molmod/stacie\">Unreleased</a><a class=\"headerlink\" href=\"#unreleased\" title=\"Link to this heading\">\u00b6</a></h2><p>(no changes yet)</p>", "a[href=\"#changelog\"]": "<h1 class=\"tippy-header\" style=\"margin-top: 0;\">Changelog<a class=\"headerlink\" href=\"#changelog\" title=\"Link to this heading\">\u00b6</a></h1><p>All notable changes to this project will be documented in this file.</p><p>The format is based on <a class=\"reference external\" href=\"https://keepachangelog.com/en/1.1.0/\">Keep a Changelog</a>,\nand this project adheres to <a class=\"reference external\" href=\"https://jacobtomlinson.dev/effver/\">Effort-based Versioning</a>.</p>", "a[href=\"#id13\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\">Changed<a class=\"headerlink\" href=\"#id13\" title=\"Link to this heading\">\u00b6</a></h3>", "a[href=\"#added\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\">Added<a class=\"headerlink\" href=\"#added\" title=\"Link to this heading\">\u00b6</a></h3>", "a[href=\"#fixed\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\">Fixed<a class=\"headerlink\" href=\"#fixed\" title=\"Link to this heading\">\u00b6</a></h3>", "a[href=\"#id5\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\">Changed<a class=\"headerlink\" href=\"#id5\" title=\"Link to this heading\">\u00b6</a></h3>", "a[href=\"#v1-1-1\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\"><a class=\"reference external\" href=\"https://github.com/molmod/stacie/releases/tag/v1.1.1\">1.1.1</a> - 2025-11-18<a class=\"headerlink\" href=\"#v1-1-1\" title=\"Link to this heading\">\u00b6</a></h2><p>Smaller source package (10x size reduction) and dependency simplification.</p>", "a[href=\"#id9\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\">Changed<a class=\"headerlink\" href=\"#id9\" title=\"Link to this heading\">\u00b6</a></h3>"}
skip_classes = ["headerlink", "sd-stretched-link"]

window.onload = function () {
    for (const [select, tip_html] of Object.entries(selector_to_html)) {
        const links = document.querySelectorAll(` ${select}`);
        for (const link of links) {
            if (skip_classes.some(c => link.classList.contains(c))) {
                continue;
            }

            tippy(link, {
                content: tip_html,
                allowHTML: true,
                arrow: true,
                placement: 'auto-start', maxWidth: 500, interactive: false,

            });
        };
    };
    console.log("tippy tips loaded!");
};
